mapper.select:
SELECT A.ID , A.FILE_NAME fileName, A.FILE_PATH filePath, A.FILE_SIZE fileSize, A.FILE_TYPE fileType, A.OPERATOR AS operator, A.FILE_NUMBER, A.FILE_STATE, A.BUSINESS_FILE_TYPE, B.NICKNAME operatorName, A.OPERATE_TIME operateTime, C.ID systemId, C.SUBSYSTEM_NAME systemName, A.DOWNLOAD_TIMES downloadTimes, A.REMARK remark, c.app_id appId FROM SYS_FILE_MANAGE A LEFT JOIN SYS_USER B ON A.OPERATOR = B.ID AND B.DEL_FLAG = 'N' LEFT JOIN SYS_SUBSYSTEM C ON (A.SYSTEM = C.ID OR A.SYSTEM = C.APP_ID) AND C.DEL_FLAG = 'N'
and POSITION (#{fileName} in A.file_name)>0
and A.FILE_TYPE like '%' || #{fileType} || '%'
and A.CREATE_USER_ID = #{createUserId}
and B.NICKNAME LIKE '%' || #{operatorName} || '%'
and A.FILE_NUMBER = #{fileNumber}
and A.FILE_STATE = #{fileState}
and A.BUSINESS_FILE_TYPE = #{businessFileType}
AND A.ACTIVATION_STATE = 'Y' AND A.DEL_FLAG = 'N'
and ( C.ID = #{systemId} OR C.APP_ID = #{systemId} )
order by OPERATE_TIME desc

mapper.update:
update sys_file_manage set UPDATE_TIME = current_timestamp,DEL_FLAG='Y',UPDATE_USER_ID=#{updateUserId},OPERATOR = #{updateUserId},OPERATE_TIME = current_timestamp
where id in

    #{id}

    update SYS_FILE_MANAGE set DOWNLOAD_TIMES = DOWNLOAD_TIMES + 1
    where id in
    (
    #{id}
)

mapper.select:
select count(1) from SYS_FILE_MANAGE a
where a.DEL_FLAG = 'N' and (a.SYSTEM = #{appId} or a.SYSTEM = #{systemId})
and a.FILE_NAME = #{fileName}

    AND a.id != #{fileId}
