mapper.select:
SELECT SO.*  FROM SYS_ORG SO
INNER JOIN SYS_ORG_AGENT SOA ON SO.ID=SOA.THIRD_ORG_ID
LEFT JOIN SYS_SUBSYSTEM ss on SOA.SUBSYSTEM_ID = SS.ID OR SOA.SUBSYSTEM_ID =  SS.app_id


    AND (ss.ID=#{subsystemId}  or  ss.APP_ID=#{subsystemId} )


    AND SOA.ORG_ID=#{orgId}


    AND SOA.AGENT_CODE=#{agentCode}

SELECT SO.*  FROM SYS_ORG SO
INNER JOIN SYS_ORG_AGENT SOA ON SO.ID=SOA.ORG_ID
LEFT JOIN SYS_SUBSYSTEM ss on (SOA.SUBSYSTEM_ID = SS.ID OR SOA.SUBSYSTEM_ID =  SS.app_id)


    AND (ss.ID=#{subsystemId}  or  ss.APP_ID=#{subsystemId} )


    AND SOA.THIRD_ORG_ID=#{agentOrgId}


    AND SOA.AGENT_CODE=#{agentCode}

select distinct sr.ID regionId, sr.region_name regionName, so.ID orgId, so.organization_name orgName, SO.phone orgPhone, pro_so.ID proxyId, pro_so.organization_name proxyOrgName, pro_so.phone proxyPhone, soa.status status from SYS_ORG_AGENT soa left join SYS_ORG so on soa.ORG_ID = SO.ID and so.del_flag != 'Y' left join SYS_REGION sr on so.REGION_LAST_CODE = sr.ID and sr.del_flag != 'Y' left join SYS_ORG pro_so on soa.third_org_id = pro_so.ID and pro_so.del_flag != 'Y' left join SYS_SUBSYSTEM ss on (soa.subsystem_id = ss.ID or soa.subsystem_id = ss.app_id) and ss.del_flag != 'Y' where 1=1
and SO.ORGANIZATION_LEVEL = #{orgLevel}
and (SOA.subsystem_id = #{appId} or ss.app_id = #{appId})
and pro_so.ID = #{proxyId}
and so.ID = #{orgId}
and sr.ID = #{regionId}
and soa.agent_code = #{agentCode}
