mapper.select:
SELECT

from SYS_ORG so
LEFT JOIN SYS_SYSTEM_ORG SSO ON SSO.ORG_ID = SO.ID
where
so.DEL_FLAG = 'N'
AND SSO.APP_ID = #{appId}
AND SO.ID IN (
select org_id from SYS_SYSTEM_FUNCTION where function_code = (
select SD.id from SYS_DICT sd left join SYS_DICT_TYPE sdt on SD.DICTTYPEID = SDT.ID
where sdt.TYPEVALUE = 'sys_org_function' and sd.dictcode = #{functionCode}
) and APP_ID = #{appId}
)

select distinct so.ID, so.PARENT_ID, so.ORGANIZATION_NAME, so.PARENT_IDS, so.DEL_FLAG, so.ORGANIZATION_LEVEL, so.THIRD_ORG, so.ADDRESS_DETAIL, so.PRINCIPAL, so.PHONE, so.SORT, so.REGION_LAST_CODE, so.ADDRESS_LAST_CODE, SO.longitude, SO.latitude, so.create_time from ( select * from SYS_ORG where PARENT_IDS like (#{otherParam.parentIdStrs} || '%') union select * from sys_org a where a.ID = #{otherParam.id} ) so LEFT JOIN SYS_SYSTEM_ORG SSO ON SSO.ORG_ID = SO.ID LEFT JOIN SYS_SUBSYSTEM SS ON SS.APP_ID =SSO.APP_ID WHERE SO.DEL_FLAG = 'N' AND (SSO.APP_ID = #{appId} OR SS.ID = #{appId})
and so.ORGANIZATION_NAME LIKE '%'|| #{otherParam.orgName} ||'%'
and so.REGIONCODE like '%' || #{otherParam.regionCode} || '%'
and SO.ORGANIZATION_LEVEL = #{otherParam.orgLevel}
and so.THIRD_ORG = #{otherParam.thirdOrg}
order by so.create_time desc , so.id DESC

