mapper.select:
SELECT count(1) from SYS_SUBSYSTEM a where 1 =1

    and SUBSYSTEM_NAME = #{name}



    and APP_ID = #{appId}



    and ID != #{id}

and DEL_FLAG = 'N'

SELECT ID , PARENT_ID , SUBSYSTEM_NAME , APP_ID , DESCRIPTION , PARENT_IDS, CREATE_USER_ID , CREATE_TIME , UPDATE_USER_ID , UPDATE_TIME , DEL_FLAG , sort, VIEW_URL, SYSTEM_TYPE FROM SYS_SUBSYSTEM where ID IN
#{item}
and DEL_FLAG != 'Y'

select * from SYS_SUBSYSTEM ss where ss.APP_ID in(
select DISTINCT(sr.App_Id) from SYS_RESOURCE  sr
LEFT JOIN SYS_ROLE_RESOURCE srr on sr.id=srr.resource_id
where srr.ROLE_ID in

    #{id}

) and ss.del_flag != 'Y'

select SS.ID id, SS.PARENT_ID parentId, SS.PARENT_IDS parentIds, SS.SUBSYSTEM_NAME subsystemName, SS.APP_ID appId, SS.DESCRIPTION description, SS.SORT sort, SS.VIEW_URL viewUrl, SS.SYSTEM_TYPE systemType FROM SYS_SUBSYSTEM SS where (SS.DEL_FLAG = 'N'OR SS.DEL_FLAG IS NULL)
and ID in
#{id}
and SS.PARENT_IDS like #{parentIds} || '%'
and SS.PARENT_ID in
#{parentId}
