mapper.select:
select * FROM ( SELECT tmp_page.*, rownum row_id FROM ( SELECT t.id, t.username, t.password, t.role, t.permission FROM xxl_job_user t
AND t.username like '%' || #{username} || '%'
AND t.role = #{role}
ORDER BY username ASC ) tmp_page WHERE rownum <= (#{offset} + #{pagesize}) ) WHERE row_id > #{offset}

SELECT count(1) FROM xxl_job_user t
AND t.username like '%' || #{username} || '%'
AND t.role = #{role}

SELECT 
FROM xxl_job_user t
WHERE t.username = #{username}

mapper.update:
UPDATE xxl_job_user
SET

		password = #{password},

	role = #{role},
	permission = #{permission}
WHERE id = #{id}
